import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Clock, 
  Heart, 
  Download, 
  Eye, 
  Search,
  TrendingUp,
  TrendingDown,
  AlertTriangle
} from 'lucide-react';
import toast from 'react-hot-toast';

const History = () => {
  const navigate = useNavigate();
  const [predictions, setPredictions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRisk, setFilterRisk] = useState('all');
  const [sortBy, setSortBy] = useState('date');

  useEffect(() => {
    fetchPredictionHistory();
  }, []);

  const fetchPredictionHistory = async () => {
    try {
      const response = await fetch('/api/prediction/history', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        const list = Array.isArray(data)
          ? data
          : (Array.isArray(data.predictions) ? data.predictions : []);
        setPredictions(list);
      } else {
        throw new Error('Failed to fetch history');
      }
    } catch (error) {
      console.error('Error fetching history:', error);
      toast.error('Failed to load prediction history');
    } finally {
      setIsLoading(false);
    }
  };

  const getRiskLevel = (prob) => {
    const probability = Number(prob);

    if (probability < 0.3)
      return { level: 'Low', color: 'text-green-600', bgColor: 'bg-green-100' };

    if (probability < 0.7)
      return { level: 'Moderate', color: 'text-yellow-600', bgColor: 'bg-yellow-100' };

    return { level: 'High', color: 'text-red-600', bgColor: 'bg-red-100' };
  };

  const getRiskIcon = (prob) => {
    const probability = Number(prob);

    if (probability < 0.3) return <TrendingDown className="w-5 h-5 text-green-600" />;
    if (probability < 0.7) return <AlertTriangle className="w-5 h-5 text-yellow-600" />;
    return <TrendingUp className="w-5 h-5 text-red-600" />;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const downloadReport = () => {
    toast.success('Report download started');
  };

  const viewDetails = (prediction) => {
    navigate('/prediction-result', { state: { prediction } });
  };

  // -------------------------------
  // FILTER + SORT
  // -------------------------------
  const filteredAndSortedPredictions = predictions
    .filter(prediction => {
      
      const ageStr = prediction.inputData?.age?.toString() || "";

      const sex = prediction.inputData?.sex;
      const gender =
        sex === 1 ? "male" :
        sex === 0 ? "female" :
        (sex || "").toString().toLowerCase();

      const search = searchTerm.toLowerCase();

      const matchesSearch =
        ageStr.includes(search) ||
        gender.includes(search);

      const risk = getRiskLevel(prediction.predictionResult?.probability).level.toLowerCase();
      const matchesRisk = filterRisk === "all" || risk === filterRisk.toLowerCase();

      return matchesSearch && matchesRisk;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case "date":
          return new Date(b.createdAt) - new Date(a.createdAt);
        case "risk":
          return Number(b.predictionResult?.probability) - Number(a.predictionResult?.probability);
        case "age":
          return Number(a.inputData?.age) - Number(b.inputData?.age);
        default:
          return 0;
      }
    });

  // -------------------------------
  // STATS
  // -------------------------------
  const getStats = (list) => {
    if (!list || list.length === 0)
      return { total: 0, lowRisk: 0, moderateRisk: 0, highRisk: 0 };

    let lowRisk = 0,
        moderateRisk = 0,
        highRisk = 0;

    list.forEach(p => {
      const pr = Number(p.predictionResult?.probability);
      if (pr < 0.3) lowRisk++;
      else if (pr < 0.7) moderateRisk++;
      else highRisk++;
    });

    return { total: list.length, lowRisk, moderateRisk, highRisk };
  };

  const stats = getStats(filteredAndSortedPredictions);

  // -------------------------------
  // LOADING UI
  // -------------------------------
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading prediction history...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Prediction History</h1>
          <p className="text-gray-600">Track your heart health predictions over time</p>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <StatCard icon={<Clock className="w-6 h-6 text-blue-600" />} title="Total Predictions" value={stats.total} bg="bg-blue-100" />
          <StatCard icon={<TrendingDown className="w-6 h-6 text-green-600" />} title="Low Risk" value={stats.lowRisk} bg="bg-green-100" />
          <StatCard icon={<AlertTriangle className="w-6 h-6 text-yellow-600" />} title="Moderate Risk" value={stats.moderateRisk} bg="bg-yellow-100" />
          <StatCard icon={<TrendingUp className="w-6 h-6 text-red-600" />} title="High Risk" value={stats.highRisk} bg="bg-red-100" />
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search by age, gender..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input pl-10 w-full"
              />
            </div>

            <div className="flex gap-4">
              <select value={filterRisk} onChange={(e) => setFilterRisk(e.target.value)} className="input">
                <option value="all">All Risk Levels</option>
                <option value="low">Low Risk</option>
                <option value="moderate">Moderate Risk</option>
                <option value="high">High Risk</option>
              </select>

              <select value={sortBy} onChange={(e) => setSortBy(e.target.value)} className="input">
                <option value="date">Sort by Date</option>
                <option value="risk">Sort by Risk</option>
                <option value="age">Sort by Age</option>
              </select>
            </div>
          </div>
        </div>

        {/* MAIN LIST */}
        <div className="bg-white rounded-lg shadow">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-900">
              Recent Predictions ({filteredAndSortedPredictions.length})
            </h2>
          </div>

          {filteredAndSortedPredictions.length === 0 ? (
            <EmptyState navigate={navigate} predictions={predictions} />
          ) : (
            <div className="divide-y divide-gray-200">
              {filteredAndSortedPredictions.map((prediction) => {
                const riskInfo = getRiskLevel(prediction.predictionResult?.probability);
                return (
                  <PredictionRow
                    key={prediction._id}
                    prediction={prediction}
                    riskInfo={riskInfo}
                    getRiskIcon={getRiskIcon}
                    formatDate={formatDate}
                    viewDetails={viewDetails}
                    downloadReport={downloadReport}
                  />
                );
              })}
            </div>
          )}
        </div>

        <div className="mt-8 text-center">
          <button onClick={() => navigate('/prediction')} className="btn btn-primary">
            <Heart className="w-5 h-5 mr-2" />
            Make New Prediction
          </button>
        </div>

      </div>
    </div>
  );
};

/* ----------------------
    COMPONENTS
----------------------- */

const StatCard = ({ icon, title, value, bg }) => (
  <div className="bg-white rounded-lg shadow p-6">
    <div className="flex items-center">
      <div className={`p-2 rounded-lg ${bg}`}>{icon}</div>
      <div className="ml-4">
        <p className="text-sm font-medium text-gray-600">{title}</p>
        <p className="text-2xl font-semibold text-gray-900">{value}</p>
      </div>
    </div>
  </div>
);

const EmptyState = ({ navigate, predictions }) => (
  <div className="text-center py-12">
    <Heart className="w-16 h-16 text-gray-300 mx-auto mb-4" />
    <h3 className="text-lg font-medium text-gray-900 mb-2">
      {predictions.length === 0 ? "No predictions yet" : "No predictions match your filters"}
    </h3>
    <p className="text-gray-500 mb-4">
      {predictions.length === 0
        ? "Start by making your first heart disease prediction"
        : "Try adjusting your search criteria"}
    </p>
    {predictions.length === 0 && (
      <button onClick={() => navigate('/prediction')} className="btn btn-primary">
        Make First Prediction
      </button>
    )}
  </div>
);

const PredictionRow = ({ prediction, riskInfo, getRiskIcon, formatDate, viewDetails, downloadReport }) => {

